CREATE TABLE factura (id BIGSERIAL, razon_social VARCHAR(250) NOT NULL, nit VARCHAR(12) NOT NULL, numero BIGINT NOT NULL, numero_autorizacion BIGINT NOT NULL, fecha DATE NOT NULL, importe_total BIGINT, codigo_control VARCHAR(250) NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE item (id BIGSERIAL, factura_id BIGINT NOT NULL, detalle VARCHAR(250) NOT NULL, precio_unitario BIGINT NOT NULL, cantidad BIGINT NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE producto (id BIGSERIAL, nombre VARCHAR(250) NOT NULL, modelo VARCHAR(250), marca VARCHAR(250), detalle VARCHAR(250), costo BIGINT NOT NULL, clasificacion_id BIGINT NOT NULL, stock BOOLEAN DEFAULT 'false', created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
ALTER TABLE item ADD CONSTRAINT item_factura_id_factura_id FOREIGN KEY (factura_id) REFERENCES factura(id) NOT DEFERRABLE INITIALLY IMMEDIATE;
